export function createRoot (
  container: Element | DocumentFragment,
  options?: CreateRootOptions,
): RootType {
  console.log({ container, options }, "传入container根节点和可选的options配置对象");
  // 判断传入的root节点是否是dom节点
  console.log("校验container是否是dom节点")
