  }

  const root = createContainer(
    container,
    ConcurrentRoot,
    null,
    isStrictMode,
    concurrentUpdatesByDefaultOverride,
    identifierPrefix,
    onRecoverableError,
    transitionCallbacks,
  );
  // markContainerAsRoot 方法在容器 DOM 节点上添加一个属性 __reactContainer${randomKey}，属性的值指向Fiber HostRoot节点。以表明该 DOM 节点为当前 React 应用的容器节点。
